const url = 'https://baraxolka-kz-default-rtdb.asia-southeast1.firebasedatabase.app/goods.json'

async function goods() {
    try {
        const req = await fetch(url)
        const res = await req.json()
        const arr = Object.values(res)
        return arr
    }
    catch {
        return 'Ошибка получения списка товаров'
    }
}  

export default goods
