import { useState, useEffect } from 'react'


const [goodList, setGoodList] = useState()
useEffect(() => getGoods().then(data => setGoodList(data)), [])

const goods = goodList

async function getGoods() {
    try {
        const req = await fetch('https://baraxolka-kz-default-rtdb.asia-southeast1.firebasedatabase.app/goods.json')
        const res = await req.json()
        return Object.values(res)
    }
    catch {
        return 'Ошибка получения списка товаров'
    }
}  

export default goods
