import React from 'react'


const Text = ({ good }) => {
    const options = Object.keys(good).filter(item => item !== 'Артикул' && item !== 'Состояние' && item !== 'Цена'
        && item !== 'Количество' && item !== 'Название' && item !== 'Фото')

    return (
        <div className="text">
            <h2>{goodName()}</h2>
            <h3>Артикул: <span>{good['Артикул']}</span></h3>
            <h3 className="price">Цена: <span>{good['Цена']} тг</span></h3>

            <table>
                <tbody>
                    {options.map(item => {
                        return (
                            <tr key={item}>
                                <td>{item}</td>
                                <td>
                                    {good[item]}
                                    {item === 'Частота ядра' && ' ГГц'}
                                    {item === 'Частота системной шины' && ' МГц'}
                                    {item === 'Объем кэш-памяти' && ' МБ'}
                                    {item === 'Мощность тепловыделения' && ' Вт'}
                                </td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>

            <div className="buttons">
                <div className="tocart">В корзину</div>
                <div className="tofavorites active">В избранном</div>
            </div>
        </div>
    )

    function goodName() {
        let title = good['Название']

        const artcl = good['Артикул'][0]

        switch(artcl) {
            case '2':
                title = good['Тип ОЗУ'] + ' ' + good['Бренд'] + ' ' + good['Объем ОЗУ']
                break
            case '4':
                title = good['Тип накопителя'] + ' ' + good['Бренд'] + ' ' + good['Объем']
                break
            case '5':
                title += ' ' + good['Мощность'] + ' Вт'
                break
            case '6':
                title += !good['Сокет'] ? ' ' + good['Диаметр'] + ' мм' : ''
                break
            default:
                break
        }

        return title
    }
}

export default Text
