import React from 'react'


const ToFavorites = ({ goodArticle }) => {
    const favorites = localStorage.getItem('favorites')
    const btnClass = favorites.includes(goodArticle) ? 'tofavorites active' : 'tofavorites'
    const btnText = favorites.includes(goodArticle) ? 'В избранном' : 'В избранное'

    return (
        <div className={btnClass}>{btnText}</div>
    )
}

export default ToFavorites
