const goods = getGoods()

async function getGoods(resolve) {
    try {
        const req = await fetch('https://baraxolka-kz-default-rtdb.asia-southeast1.firebasedatabase.app/goods.json')
        const res = await req.json()
        return resolve(Object.values(res))
    }
    catch {
        return 'Ошибка получения списка товаров'
    }
}  

export default goods
