import React from 'react'


const InCart = () => {
    !localStorage.getItem('cost') && localStorage.setItem('cost', JSON.stringify({}))
    const goodsCost = JSON.parse(localStorage.getItem('cost'))

    return Object.keys(goodsCost).length !== 0
        ? <div className="incart">
            <h3 className="sum">Итого: {getSumCost()} тг</h3>
            <h3 className="sale">Оформить заказ</h3>
        </div>
        : ''

    function getSumCost() {
        console.log(Object.keys(goodsCost).map(data => data.cost))
        return Object.keys(goodsCost).map(data => data.cost).reduce((a, b) => a + b, 0)
    }
}

export default InCart
