import React, { useState } from 'react'


const ToFavorites = ({ goodArticle }) => {
    const favorites = localStorage.getItem('favorites')

    const [isFavorite, setIsFavorite] = useState(favorites.includes(goodArticle))

    const btnClass = isFavorite ? 'tofavorites active' : 'tofavorites'
    const btnText = isFavorite ? 'В избранном' : 'В избранное'

    return (
        <div className={btnClass} onClick={toggleFavorite}>{btnText}</div>
    )


    function toggleFavorite() {
        setIsFavorite(!isFavorite)
        favorites.includes(goodArticle)
            ? localStorage.setItem('favorites', JSON.stringify(favorites.filter(artcl => artcl !== goodArticle)))
            : localStorage.setItem('favorites', JSON.stringify(favorites.push(goodArticle)))
    }
}

export default ToFavorites
