import React, { useState } from 'react'


const ToCart = ({ goodArticle }) => {
    let cart = JSON.parse(localStorage.getItem('cart'))

    const [inCart, setInCart] = useState(cart.includes(goodArticle))

    const btnClass = inCart ? 'tocart active' : 'tocart'
    const btnText = inCart ? 'В корзине' : 'В корзину'

    return (
        <div className={btnClass} onClick={toggleCart}>{btnText}</div>
    )


    function toggleCart() {
        setIsFavorite(!inCart)

        if(cart.includes(goodArticle)) {
            cart = cart.filter(artcl => artcl !== goodArticle)
        }
        else {
            cart.push(goodArticle)
        }
        localStorage.setItem('cart', JSON.stringify(cart))
    }
}

export default ToCart
