import MySelect from './mySelect'
import goods from '../../../db/goods'


const Condition = ({ category, filters, onSelect }) => {
    const goodList = goods.filter(good => good['Количество'] !== '0' && good['Артикул'][0] === category)
    const vars = ['Все', 'Новое', 'Отличное', 'Хорошее', 'Среднее']

    let arr = []  // список имеющихся вариантов товара в наличии
    goodList.forEach(good => {
        if(good['Состояние'].includes('/')) {
            good['Состояние'].split('/').forEach(fsbGood => !arr.includes(fsbGood) && arr.push(fsbGood))
