import React, { useState, useEffect } from 'react'
import goods from '../../../db/goods'
import Pagination from '../pagination'
import Tiser from './tiser'
import _ from 'lodash'


const TiserList = ({ category, search, isFavorites, favorites, onFavorites, cart, onCart, isSorting, filters }) => {
    const [currentPage, setCurrentPage] = useState(1)
    useEffect(() => setCurrentPage(1), [category])

    const [goodsFromDB, setGoodsFromDb] = useState()
    useEffect(() => goods.then(data => setGoodsFromDb(data)), [])

    let goodList = goodsFromDB || []
    if(typeof goodList === 'string') return <h2 className='not_found'>{goodList}</h2>

    goodList = goodList.filter(good => good['Количество'] !== '0')

    category && setGoodList(goodList.filter(good => good['Артикул'][0] === category))

    search && setGoodList(goodList.filter(good => (good['Название'] && good['Название'].toLowerCase().includes(search.toLowerCase()))
        || (good['Бренд'] && good['Бренд'].toLocaleLowerCase().includes(search.toLocaleLowerCase()))))

    isFavorites && setGoodList(goodList.filter(good => favorites.includes(good['Артикул'])))

    filters.condition.value !== 'Все' && setGoodList(goodList.filter(good => good['Состояние'] === filters.condition.value))

    if(category === '6') {
        if(filters.typeCooler.value === 'Процессорный') {
            setGoodList(goodList.filter(good => good['Сокет']))
        }
        else if(filters.typeCooler.value === 'Корпусной') {
            setGoodList(goodList.filter(good => !good['Сокет']))
        }
    }

    if(['0', '1', '6', '7', '8'].includes(category)) {
        filters.socket.value !== 'Все' && setGoodList(goodList.filter(good => isHasSocket(good)))
    }

    if(category === '0') {
        filters.core.value !== 'Все' && setGoodList(goodList.filter(good => good['Ядер\\потоков'].split('/')[0] === filters.core.value))
        filters.cpuFrequency.value !== 'Все' && setGoodList(goodList.filter(good => isHasFrequency(good)))
        filters.tdp.value !== 'Все' && setGoodList(goodList.filter(good => Number(good['Мощность тепловыделения']) === filters.tdp.value))
    }

    if(['0', '1'].includes(category)) {
        filters.fsb.value !== 'Все' && setGoodList(goodList.filter(good => isHasFSB(good)))
    }

    if(['2', '4'].includes(category)) {
        filters.platform.value !== 'Все' && setGoodList(goodList.filter(good => good['Платформа'] === filters.platform.value))
    }

    if(['1', '2', '3'].includes(category)) {
        filters.ramType.value !== 'Все' && setGoodList(goodList.filter(good => good['Тип ОЗУ'] === filters.ramType.value))
        filters.ramSize.value !== 'Все' && setGoodList(goodList.filter(good => isHasRAMSize(good)))
    }

    if(['1', '2'].includes(category)) {
        filters.ramFraq.value !== 'Все' && setGoodList(goodList.filter(good => isHasRAMFraq(good)))
    }

    if(['1', '3'].includes(category)) {
        filters.videoInterface.value !== 'Все' && setGoodList(goodList.filter(good => good['Тип видео-интерфейса'] === filters.videoInterface.value))
    }

    if(['1', '4'].includes(category)) {
        filters.hddInterface.value !== 'Все' && setGoodList(goodList.filter(good => isHasHDDInterface(good)))
    }

    if(category === '3') {
        filters.videoBitrate.value !== 'Все' && setGoodList(goodList.filter(good => good['Разрядность шины'] === filters.videoBitrate.value))
    }

    if(category === '4') {
        filters.hddType.value !== 'Все' && setGoodList(goodList.filter(good => good['Тип накопителя'] === filters.hddType.value))
        filters.hddSize.value !== 'Все' && setGoodList(goodList.filter(good => good['Объем'] === filters.hddSize.value))
    }

    if(category === '5') {
        filters.bpPower.value !== 'Все' && setGoodList(goodList.filter(good => good['Мощность'] === filters.bpPower.value))
    }

    const sortPriceGoodList = isSorting ? _.orderBy(goodList, ['Цена'], ['asc']) : goodList

    const allNumberPage = goodList && Math.ceil(goodList.length / 12)
    const currentGoodList = goodList && sortPriceGoodList.slice((currentPage - 1) * 12, currentPage * 12)


    return goodList && currentGoodList.length !== 0
        ? <>
            {currentGoodList.map(good => <Tiser data={good}
                favorites={favorites} onFavorites={onFavorites}
                cart={cart} onCart={onCart}
                key={good['Артикул']} />)}
            {allNumberPage > 1 && <Pagination current={currentPage} all={allNumberPage} onPage={handleChangePage} />}
        </>
        : <h2 className='not_found'>Ничего не найдено.<br/>Попробуйте изменить условия фильтрации.</h2>

    function isHasSocket(good) {
        if(category !== '6') {
            return good['Сокет'] === filters.socket.value
        }
        else {
            if(good['Сокет']) {
                if(good['Сокет'].includes('/')) {
                    let arrSockets = good['Сокет'].split('/')
                    arrSockets = arrSockets.map(item => item.trim())
                    return arrSockets.includes(filters.socket.value)
                }
                else {
                    return good['Сокет'] === filters.socket.value
                }
            }
        }
    }

    function isHasFrequency(good) {
        const freq = Number(good['Частота ядра'])

        switch(filters.cpuFrequency.value) {
            case 'Ниже 2 ГГц':
                return freq < 2
            case '2-2.4 ГГц':
                return freq >= 2 && freq < 2.5
            case '2.5-2.9 ГГЦ':
                return freq >= 2.5 && freq < 3
            case 'От 3 ГГЦ и выше':
                return freq >= 3
            default:
                break
        }
    }

    function isHasFSB(good) {
        if(good['Частота системной шины'].includes('/')) {
            return good['Частота системной шины'].split('/').includes(filters.fsb.value)
        }
        else {
            return good['Частота системной шины'] === filters.fsb.value
        }
    }


    function isHasRAMSize(good) {
        if(good['Объем ОЗУ'].includes('x')) {
            const size = good['Объем ОЗУ'][0] + ' x ' + good['Объем ОЗУ'][2] + ' ГБ'
            return size === filters.ramSize.value
        }
        else {
            return good['Объем ОЗУ'] === filters.ramSize.value
        }
    }

    function isHasRAMFraq(good) {
        if(good['Частота ОЗУ'].includes('/')) {
            return good['Частота ОЗУ'].split('/').includes(filters.ramFraq.value)
        }
        else {
            return good['Частота ОЗУ'] === filters.ramFraq.value
        }
    }

    function isHasHDDInterface(good) {
        if(good['Тип HDD-интерфейса'].includes('/')) {
            return good['Тип HDD-интерфейса'].split('/').includes(filters.hddInterface.value)
        }
        else {
            return good['Тип HDD-интерфейса'] === filters.hddInterface.value
        }
    }

    function handleChangePage(numberPage) {
        setCurrentPage(numberPage)
    }
}

export default TiserList
